import React, { useEffect, useRef, useState } from 'react';
import 'ol/ol.css';
import { Map, View } from 'ol';
import TileLayer from 'ol/layer/Tile';
import XYZ from 'ol/source/XYZ';
import { fromLonLat } from 'ol/proj';
import VectorLayer from 'ol/layer/Vector';
import VectorSource from 'ol/source/Vector';
import { Style, Fill, Stroke } from 'ol/style';
import { GeoJSON } from 'ol/format';
import Overlay from 'ol/Overlay';

const densityRanges = [
    { range: '0-25', color: '#FFEDA0' },
    { range: '25-50', color: '#FED976' },
    { range: '50-100', color: '#FEB24C' },
    { range: '100-250', color: '#FD8D3C' },
    { range: '250-500', color: '#FC4E2A' },
    { range: '500-750', color: '#E31A1C' },
    { range: '750-1000', color: '#BD0026' },
    { range: '1000+', color: '#800026' },
];

const getDensity = (feature: any) => {
    const density = feature.get('density');
    return density !== undefined ? density : 0;
};

const MapComponent: React.FC = () => {
    const mapElement = useRef<HTMLDivElement | null>(null);
    const popupElement = useRef<HTMLDivElement | null>(null);
    const [popupContent, setPopupContent] = useState<string>('');
    const [popupPosition, setPopupPosition] = useState<number[]>([]);
    const [showDensity, setShowDensity] = useState<boolean>(true);
    const [selectedRange, setSelectedRange] = useState<string | null>(null);

    useEffect(() => {
        if (!mapElement.current || !popupElement.current) return;

        const vectorSource = new VectorSource({
            url: 'https://openlayers.org/data/vector/us-states.json',
            format: new GeoJSON(),
        });

        const vectorLayer = new VectorLayer({
            source: vectorSource,
            style: (feature) => {
                const density = getDensity(feature);
                const color = showDensity ? getColor(density) : 'rgba(0, 0, 255, 0.1)';
                return new Style({
                    fill: new Fill({
                        color: color,
                    }),
                    stroke: new Stroke({
                        color: '#319FD3',
                        width: 1,
                    }),
                });
            },
        });

        const getColor = (density: number) => {
            if (selectedRange) {
                const [min, max] = selectedRange.split('-').map(Number);
                if (max) {
                    if (density >= min && density < max) {
                        return densityRanges.find(range => range.range === selectedRange)?.color || 'rgba(0, 0, 255, 0.1)';
                    }
                } else {
                    if (density >= min) {
                        return densityRanges.find(range => range.range === selectedRange)?.color || 'rgba(0, 0, 255, 0.1)';
                    }
                }
                return 'rgba(0, 0, 255, 0.1)'; // Color for states not in the selected range
            }
            return densityRanges.find(range => range.range === getRange(density))?.color || 'rgba(0, 0, 255, 0.1)';
        };

        const getRange = (density: number) => {
            for (const range of densityRanges) {
                const [min, max] = range.range.split('-').map(Number);
                if (max) {
                    if (density >= min && density < max) {
                        return range.range;
                    }
                } else {
                    if (density >= min) {
                        return range.range;
                    }
                }
            }
            return '0-25';
        };

        const overlay = new Overlay({
            element: popupElement.current!,
            autoPan: {
                animation: {
                    duration: 250,
                },
            },
        });

        const map = new Map({
            target: mapElement.current!,
            layers: [
                new TileLayer({
                    source: new XYZ({
                        url: 'https://{a-c}.tile.openstreetmap.org/{z}/{x}/{y}.png',
                    }),
                }),
                vectorLayer,
            ],
            view: new View({
                center: fromLonLat([-95.7129, 37.0902]), // Centered on the USA
                zoom: 4.4, // Increase zoom level by 10%
            }),
            overlays: [overlay],
        });

        const handlePointerMove = (event: any) => {
            map.getTargetElement().style.cursor = map.hasFeatureAtPixel(event.pixel) ? 'pointer' : '';
            const feature = map.getFeaturesAtPixel(event.pixel)[0];
            if (feature) {
                const coordinates = event.coordinate;
                const name = feature.get('name');
                setPopupContent(`State: ${name}`);
                setPopupPosition(coordinates);
                overlay.setPosition(coordinates);
            } else {
                overlay.setPosition(undefined);
            }
        };

        map.on('pointermove', handlePointerMove);

        // Cleanup function to properly remove the map and overlay
        return () => {
            map.un('pointermove', handlePointerMove);
            map.setTarget(undefined);
            overlay.setElement(undefined);
        };
    }, [showDensity, selectedRange]);

    const toggleDensity = () => {
        setShowDensity(!showDensity);
    };

    const handleRangeClick = (range: string) => {
        setSelectedRange(range === selectedRange ? null : range);
    };

    return (
        <>
            <div ref={mapElement} style={{ width: '100%', height: '100vh' }} />
            <div ref={popupElement} id="popup" className="ol-popup" style={{ position: 'absolute', backgroundColor: 'rgba(255, 255, 255, 0.8)', padding: '5px', borderRadius: '5px', border: '1px solid black', whiteSpace: 'nowrap', transform: 'translateY(10px)', zIndex: 1000 }}>
                <div>{popupContent}</div>
            </div>
            <button onClick={toggleDensity} style={{ position: 'absolute', top: '10px', left: '10px', padding: '10px', fontSize: '16px', zIndex: 1000 }}>
                {showDensity ? 'Hide Density' : 'Show Density'}
            </button>
            <div className="legend" style={{ position: 'absolute', bottom: '10px', left: '10px', background: 'white', boxShadow: '0 0 15px rgba(0, 0, 0, 0.2)', padding: '10px', zIndex: 1000, maxHeight: '40%', overflowY: 'auto' }}>
                <h4>Population Density</h4>
                {densityRanges.map(({ range, color }) => (
                    <div key={range} style={{ display: 'flex', alignItems: 'center', marginBottom: '5px', cursor: 'pointer' }} onClick={() => handleRangeClick(range)}>
                        <span style={{ display: 'inline-block', width: '18px', height: '18px', marginRight: '8px', background: color }}></span>
                        <span style={{ textDecoration: selectedRange === range ? 'underline' : 'none' }}>{range}</span>
                    </div>
                ))}
            </div>
        </>
    );
};

export default MapComponent;
